import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { MapContainer } from "react-leaflet/MapContainer";
import { TileLayer } from "react-leaflet/TileLayer";
import { useMap } from "react-leaflet/hooks";
import { Marker } from "react-leaflet";
import { Popup } from "react-leaflet";

function IFoundAPet() {
  const [description, setDescription] = useState("");
  const [photo, setPhoto] = useState("");
  const [location, setLocation] = useState(null);
  const [time, setTime] = useState("");
  const navigate = useNavigate();

  async function petFound(e) {
    e.preventDefault();

    const token = localStorage.getItem("token");
    const foundpet = { description, photo, location, time };
    await axios.post("http://localhost:8000/auth/foundpet", foundpet, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    navigate("/getFoundPets");

    console.log(foundpet);
  }
  function covertToBase64(e) {
    console.log(e);
    let reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      console.log(reader.result);
      setPhoto(reader.result);
    };
    reader.onerror = (error) => {
      console.log("error: ", error);
    };
  }

  function LocationMarker() {
    //const 
  }

  return (
    <>
      <Form onSubmit={petFound}>
        <Form.Group className="mb-3" controlId="formBasicName">
          <Form.Label>Description</Form.Label>
          <Form.Control
            type="textarea"
            placeholder="Describe the pet here"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="formFileSm" className="mb-3">
          <Form.Label>Upload photo</Form.Label>
          <Form.Control type="file" size="sm" onChange={covertToBase64} />
          {photo === "" || photo === null ? (
            ""
          ) : (
            <img width={100} height={100} src={photo} alt="pet" />
          )}
        </Form.Group>
      </Form>
      <MapContainer
        className="map"
        style={{ width: "250px", height: "250px" }}
        center={[40.629269, 22.947412]}
        zoom={13}
        scrollWheelZoom={false}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={[40.629269, 22.947412]}>
          <Popup>
            A pretty CSS3 popup. <br /> Easily customizable.
          </Popup>
        </Marker>
      </MapContainer>
    </>
    // <div>
    //   <form onSubmit={petFound}>
    //     <label htmlFor="description">Desciption</label>
    //     <input
    //       type="text"
    //       placeholder="Describe the pet here"
    //       value={description}
    //       onChange={(e) => setDescription(e.target.value)}
    //     />
    //     <label htmlFor="photo">Photo</label>
    //     <input
    //       type="file"
    //       filename="file"
    //       accept="image/*"
    //       placeholder="Put a picture of the pet here"
    //       onChange={covertToBase64}
    //     />
    //     {photo === "" || photo === null ? (
    //       ""
    //     ) : (
    //       <img width={100} alt="pet" height={100} src={photo} />
    //     )}
    //     <label htmlFor="description">Place</label>
    //     <input
    //       type="text"
    //       placeholder="Where you found the pet"
    //       value={location}
    //       onChange={(e) => setLocation(e.target.value)}
    //     />
    //     <label htmlFor="time">Time</label>
    //     <input
    //       type="text"
    //       placeholder="When you found the pet"
    //       value={time}
    //       onChange={(e) => setTime(e.target.value)}
    //     />
    //     <button type="submit" onClick={petFound}>
    //       Submit
    //     </button>
    //   </form>
    // </div>
  );
}

export default IFoundAPet;
