import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
const jwt = require("jsonwebtoken");
const SECRET_KEY = process.env.SECRET;

function LostPetsPage() {
  const [lost, setLost] = useState([]);

  async function getAllLostPets() {
    const token = localStorage.getItem("token");
    if (!token) {
      return;
    }

    const response = await axios.get("http://localhost:8000/auth/getLostPets", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setLost(response.data);
    console.log(token)
  }

  useEffect(() => {
    getAllLostPets();
  }, []);

  const getEmailFromToken = (token) => {
    try {
      const decodedToken = jwt.verify(token, SECRET_KEY);
      const email = decodedToken.email;
      return email;
    } catch (error) {
      // Token verification failed
      console.log(error);
      return null;
    }
  };
  const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY0NmY2ODZjMzQyZjExMmJlYmU2OWQyMSIsImVtYWlsIjoiYUBnbWFpbC5jb20iLCJpYXQiOjE2ODUwMjM1NjJ9.YmgFoer1S2MI-JiVqM2VVCvEHkY1PM0bWXmkn0idhJQ";
  const email = getEmailFromToken(token);
  console.log(email);
  return (
    <div>
      {lost.map((pet) => (
        <div key={pet.id}>
          <p>{pet.name}</p>
          <p>{pet.description}</p>
          <img src={pet.photo} width={200} height={200} />
          <p>{pet.location}</p>
          <p>{pet.time}</p>

          <button>I found this pet!</button>
        </div>
      ))}
    </div>
  );
}

export default LostPetsPage;
