import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBRipple,
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
} from "mdb-react-ui-kit";

function FoundPetsPage() {
  const [founds, setFounds] = useState([]);

  async function getAllFoundPets() {
    const token = localStorage.getItem("token");
    if (!token) {
      return;
    }
    const response = await axios.get(
      "http://localhost:8000/auth/getFoundPets",
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    setFounds(response.data);
  }

  useEffect(() => {
    getAllFoundPets();
  }, []);

  return (
    <>
      {founds.map((pet) => (
        <div key={pet.id}>
          <MDBCard>
            <MDBRipple
              rippleColor="light"
              rippleTag="div"
              className="bg-image hover-overlay"
            >
              <MDBCardImage
                src={pet.photo}
                width={200}
                height={200}
                alt="pet"
                fluid
              />
            </MDBRipple>
            <MDBCardText>{pet.description}</MDBCardText>
            <MDBCardText> {pet.location && `${pet.location.lat}, ${pet.location.lng}`}</MDBCardText>
          </MDBCard>
          
        </div>
      ))}
    </>
  );
}

export default FoundPetsPage;

