import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

function FoundPetsPage() {
  const [founds, setFounds] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState("");

  async function getAllFoundPets() {
    const token = localStorage.getItem("token");
    if (!token) {
      return;
    }
    const response = await axios.get("http://localhost:8000/auth/getFoundPets", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setFounds(response.data);
  }

  useEffect(() => {
    getAllFoundPets();
  }, []);

const handleSelectedLocation = (e) =>{
  selectedLocation(e.target.value)
}

  return (
    <div>
        <label htmlFor="location">Location:</label>
        <input
          type="text"
          id="location"
          value={selectedLocation}
          onChange={handleLocationChange}
        />
      {founds.map((pet) => (
        <div key={pet.id}>
          <p>{pet.description}</p>
          <img src={pet.photo}width={200} height={200} alt="pet"/>
          <p>{pet.location&&pet.location.lat&&pet.location.lon}</p>
          <p>{pet.time}</p>
        </div>
      ))}
    </div>
  );
}

export default FoundPetsPage;
