import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBRipple,
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
} from "mdb-react-ui-kit";

function LostPetsPage() {
  const [lost, setLost] = useState([]);
  const [modal, setModal] = useState(false);
  const [petId, setPetId] = useState("");
  const token = localStorage.getItem("token");

  async function getAllLostPets() {
    if (!token) {
      return;
    }

    const response = await axios.get("http://localhost:8000/auth/getLostPets", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setLost(response.data);
  }

  useEffect(() => {
    getAllLostPets();
  });

  async function SendEmail() {
    if (!token) {
      return;
    }

    await axios
      .post(
        "http://localhost:8000/auth/send_email",
        { petId: petId },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then(() => alert("Email sent to the owner"))
      .catch(() => alert("Could not send email"));
  }

  const toggleModal = (id) => {
    setPetId(id);

    console.log(petId);
    setModal(!modal);
  };

  return (
    <>
      {lost.map((pet) => (
        <div key={pet._id}>
          <MDBCard>
          

            <MDBCardBody>
            <MDBCardImage
              src={pet.photo}
              width={200}
              height={200}
              alt="pet"
              fluid
            />
              <MDBCardTitle>{pet.name}</MDBCardTitle>
              <MDBCardText>{pet.description}</MDBCardText>
              <MDBCardText>{pet.location}</MDBCardText>
              <MDBCardText>
                {" "}
                {pet.date && pet.date.day && pet.date.month && pet.date.year
                  ? `${pet.date.day}/${pet.date.month}/${pet.date.year}`
                  : ""}
              </MDBCardText>
              <MDBCardText>{pet.time}</MDBCardText>
              <MDBBtn onClick={() => toggleModal(pet._id)}>
                I found this pet!
              </MDBBtn>
            </MDBCardBody>
          </MDBCard>
        </div>
      ))}
      <MDBModal show={modal} setShow={setModal} tabIndex="-1">
        <MDBModalDialog>
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Warning</MDBModalTitle>
              <MDBBtn
                className="btn-close"
                color="none"
                onClick={toggleModal}
              ></MDBBtn>
            </MDBModalHeader>
            <MDBModalBody>
              Pressing the button will send an email to the pets owner.Before
              you press the button make sure you have the right pet.
            </MDBModalBody>

            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={toggleModal}>
                Close
              </MDBBtn>
              <MDBBtn
                onClick={() => {
                  SendEmail();
                  toggleModal();
                }}
              >
                Send the email
              </MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  );
}
export default LostPetsPage;
