import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBRipple,
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
} from "mdb-react-ui-kit";

function LostPetsPage() {
  const [lost, setLost] = useState([]);
  const [modal, setModal] = useState(false);
  const [petId, setPetId] = useState("");
  const token = localStorage.getItem("token");

  async function getAllLostPets() {
    if (!token) {
      return;
    }

    const response = await axios.get("http://localhost:8000/auth/getLostPets", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setLost(response.data);
  }

  useEffect(() => {
    getAllLostPets();
  });

  async function SendEmail() {
    if (!token) {
      return;
    }

    await axios
      .post(
        "http://localhost:8000/auth/send_email",
        { petId: petId },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then(() => alert("Email sent to the owner"))
      .catch(() => alert("Could not send email"));
  }

  const toggleModal = (id) => {
    setPetId(id);

    console.log(petId);
    setModal(!modal);
  };

  return (
    <>
      <MDBCard>
        {lost.map((pet) => (
            <div key={pet._id}>
            <p>{pet.name}</p>
            
            <p>{pet.description}</p>
            <img src={pet.photo} width={200} height={200} alt="pet" />
            <p>{pet.location}</p>
            <p>
              {pet.date && pet.date.day && pet.date.month && pet.date.year
                ? `${pet.date.day}/${pet.date.month}/${pet.date.year}`
                : ""}
            </p>
            <p>{pet.time}</p>
            <Button onClick={() => toggleModal(pet._id)}>
              I found this pet!
            </Button>{" "}
          </div>
        ))}
      </MDBCard>
      <MDBModal show={modal} setShow={setModal} tabIndex="-1">
        <MDBModalDialog>
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Warning</MDBModalTitle>
              <MDBBtn
                className="btn-close"
                color="none"
                onClick={toggleModal}
              ></MDBBtn>
            </MDBModalHeader>
            <MDBModalBody>
              Pressing the button will send an email to the pets owner.Before
              you press the button make sure you have the right pet.
            </MDBModalBody>

            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={toggleModal}>
                Close
              </MDBBtn>
              <MDBBtn onClick={SendEmail}>Send the email</MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  );
}
export default LostPetsPage;

