import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";

function LostPetsPage() {
  const [lost, setLost] = useState([]);
  const [modal, setModal] = useState(false);
  const token = localStorage.getItem("token");

  async function getAllLostPets() {
    if (!token) {
      return;
    }

    const response = await axios.get("http://localhost:8000/auth/getLostPets", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setLost(response.data);
  }

  useEffect(() => {
    getAllLostPets();
  }, []);

  async function SendEmail() {
    if (!token) {
      return;
    }

    await axios
      .post(
        "http://localhost:8000/auth/send_email",
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then(() => alert("Email sent to the owner"))
      .catch(() => alert("Could not send email"));
  }

  const toggleModal = () => {
    setModal(!modal);
  };

  return (
    <>
      <div>
        {lost.map((pet) => (
          <div key={pet.id}>
            <p>{pet.name}</p>
            <p>{pet.description}</p>
            <img src={pet.photo} width={200} height={200} />
            <p>{pet.location}</p>
            <p>{pet.time}</p>
            {modal && (
              <div className="modal">
                <div onClick={toggleModal} className="overlay">
                  <div className="modal-content">
                    <h2>Are you sure you its the same pet?</h2>
                    <p>
                      Before you press the button please make sure you have the
                      right pet!{" "}
                    </p>
                    <button
                      onClick={() => {
                        toggleModal();
                        SendEmail();
                      }}
                    >
                      Yes send the e-mail!
                    </button>
                  </div>
                </div>
              </div>
            )}
            <button onClick={toggleModal} className="btn-modal">
              I found this pet!
            </button>
           
          </div>
        ))}
      </div>
    </>
  );
}
export default LostPetsPage;
