import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBRipple,
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
} from "mdb-react-ui-kit";

function FoundPetsPage() {
  const [founds, setFounds] = useState([]);

  async function getAllFoundPets() {
    const token = localStorage.getItem("token");
    if (!token) {
      return;
    }
    const response = await axios.get("http://localhost:8000/auth/getFoundPets", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setFounds(response.data);
  }

  useEffect(() => {
    getAllFoundPets();
  }, []);



  return (
    <div>
        
      {founds.map((pet) => (
        <div key={pet.id}>
          <p><i>Pets description: </i> {pet.description}</p>
          <img src={pet.photo}width={200} height={200} alt="pet"/>
          <p><i>Pets latitude and longitude: </i>{pet.location && `${pet.location.lat}, ${pet.location.lng}`}</p>
          <p><i>Time the pet was found: </i>{pet.time}</p>
        </div>
      ))}
    </div>
  );
}

export default FoundPetsPage;
