import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";

function LostPetsPage() {
  const [lost, setLost] = useState([]);
  const [modal, setModal] = useState(false);
  const token = localStorage.getItem("token");

  async function getAllLostPets() {
    if (!token) {
      return;
    }

    const response = await axios.get("http://localhost:8000/auth/getLostPets", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setLost(response.data);
  }

  useEffect(() => {
    getAllLostPets();
  }, []);

  async function SendEmail() {
    if (!token) {
      return;
    }

    await axios
      .post(
        "http://localhost:8000/auth/send_email",
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then(() => alert("message sent"))
      .catch(() => alert("didn't sent"));
  }

  const toggleModal = () => {
    setModal(!modal);
  };

  return (
    <div>
      {lost.map((pet) => (
        <div key={pet.id}>
          <p>{pet.name}</p>
          <p>{pet.description}</p>
          <img src={pet.photo} width={200} height={200} />
          <p>{pet.location}</p>
          <p>{pet.time}</p>
          <button onClick={toggleModal} className="btn-modal">
            I found this pet!
          </button>
          {modal && (
            <div className="modal">
              <div className="overlay">
                <div className="modal-content">
                  <h2>Hello world</h2>
                  <p>lorem </p>
                  <button onClick={toggleModal}>Close modal</button>
                </div>
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
export default LostPetsPage;
