import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import {fcmid} from "../../Firebase"
import Notification from "../Notification";


function LostPetsPage() {
  const [lost, setLost] = useState([]);
  const [notification, setNotification] = useState(null);

  async function getAllLostPets() {
    const token = localStorage.getItem("token");
    if (!token) {
      return;
    }

    const response = await axios.get("http://localhost:8000/auth/getLostPets", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setLost(response.data);
  }

  useEffect(() => {
    getAllLostPets();
  }, []);

  

  return (
    <div>
      {lost.map((pet) => (
        <div key={pet.id}>
          <p>{pet.name}</p>
          <p>{pet.description}</p>
          <img src={pet.photo} width={200} height={200}></img>
          <p>{pet.location}</p>
          <p>{pet.time}</p>
          
          {/* Add more fields as needed */}
          <button onClick={ handleNotification}>i found this pet!</button>
        </div>
      ))}
    </div>
  );
}

export default LostPetsPage;
