          value={password}
          onChange={(e) => setPassword(e.target.value)}
          id="password"
        />
        <button>Login</button>
      </form>
      {/* <button onClick={navigate}>If you haven't register yet, do it here</button> */}
    </div>
  );
}

export default Login;
